def update_task(task_id, task_update):
    tasks = get_tasks()
    for i, task in enumerate(tasks):
        if task.id == task_id:
            updated_task = Task(id=task_id, **task_update fields)
            tasks[i] = updated_task
            save_tasks(tasks)
            return updated_task
    return None
